import React from "react";
import MenuItem from "./MenuItem"

function MenuContainer({menuItems, onAddItem, category}){

    const itemComponents = menuItems.map((item) => 
    <MenuItem 
    key={item.id} 
    item={item}
    onAddItem={onAddItem}
    />)
    return(
        <>
          {category}
          <div className="menu"> {itemComponents}</div>


        </>
    )
}
export default MenuContainer;
