import React, {useEffect} from "react";
import EditForm from "../EditForm"
import OrderDetails from "../OrderDetails"
import OrderMessage from "../OrderMessage"

function OrderConfirmation({orderedItems, order, total}) {

    useEffect(() => {
        fetch("http://localhost:3001/api/v1/orders/{orders.id}")
        .then(r => r.json())
        .then((itemArray) => {
            setMenuItems(itemArray)
        })
    }, [])

    return (
        <div className="confirmation">
            <div className="edit"><EditForm /></div>
            <div className="details"><OrderDetails orderedItems={orderedItems} total={total}/></div>
            <div className="message"><OrderMessage /></div>
        </div>
    )
}

export default OrderConfirmation;
