import React from "react";
import OrderItem from "./OrderItem"

function OrderShow({addedItems}) {

    let total = 0
    addedItems.map((item) => {
       return  total = total + item.price
    })

    const orderCheckout = addedItems.map((item, index) => 
    <OrderItem
    key={index} 
    item={item} 
    />)

    return (
        <div>
            Order Show
            {orderCheckout}
            <h3>Total: ${total}</h3>
        </div>
    )
}

export default OrderShow;
