import React from "react"

function OrderItem({item, onSubtract, onAddClick}) {

    const price = item.price * item.quantity

    function handleSubtractClick(event) {
        onSubtract(item.id)
    }

    function handleAddClick(event) {
        onAddClick(item.id)
    }


    return(
        <div>
            <h4>{item.name}</h4>
          {item.quantity === 1 ? <button onClick={handleSubtractClick}>Remove</button> : <button onClick={handleSubtractClick}>-</button> } <span>{item.quantity} </span> <button onClick={handleAddClick}>+</button>
            <p>${price}</p> 
            <button>X</button>
        </div>
    )
}

export default OrderItem
