import React,{useState} from "react";

function OrderMessage({orderedItems}) {

    console.log(orderedItems)

    const [time, setTime] = useState(0)
    // let time = 0
    orderedItems.map((item) => {
        if (item.time > time){
             let itemTime = item.time
             return itemTime
        }

    })


    function dingdong() {
        if (time > 0){
            setTime(time-1)
        }

    }

    function timer() {
        setTimeout(dingdong, (time*1000))
    }

    console.log(time)

    return (
        <div>
            <h2>Thank You for Your Order!</h2>
            <h3 onLoad={timer()}>Your order will be ready in {time} minutes</h3>
        </div>
    )
}

export default OrderMessage;
